mapper.select:
select d.id, d.name, d.category_id, d.price, d.image, d.description, d.status, d.update_time, c.name categoryName from dish d left join category c on d.category_id = c.id
and d.name like concat('%',#{dishPageQueryDTO.name},'%')
and d.category_id = #{dishPageQueryDTO.categoryId}
and d.status = #{dishPageQueryDTO.status}
order by d.update_time desc

select count(*) from dish d left outer join setmeal_dish sd on d.id = sd.dish_id where d.id in
#{id}
and d.status = 1
